import React, { useState } from "react";
import api from "../api";
// handleDelete(user.id)
// renderPhrase(number)

const Users = () => {
	const [users, setUsers] = useState(api.users.fetchAll())
	cc
	const userOfUsersList = users.map((user) => {
		return(
				<tr key={user._id}>
					<td>{user.name}</td>
					<td>Quality</td>
					<td>{user.profession.name}</td>
					<td>{user.completedMeetings}</td>
					<td>{user.rate}</td>
					<td><button className="btn btn-danger" onClick = {()=>handleDelete(user._id)}>delete</button></td>
				</tr>
		);
	})
	const handleDelete = (id) => {
		setUsers(users.filter((user) => {
			return user._id !== id;
		}))
	}
	
	return (
		<>
			<h1><span className="badge bg-primary">{}тусанет с тобой сегодня</span></h1>
			<table className="table">
				<thead>
					<tr>
						<th scope="name">Имя</th>
						<th scope="quality">Качества</th>
						<th scope="profession">Профессия</th>
						<th scope="completedMeetings">Встретился,раз</th>
						<th scope="rate">Оценка</th>
						<th scope="button"></th>
					</tr>
				</thead>
				<tbody>
					{userOfUsersList}
				</tbody>
			</table>
		</>
	);
}

export default Users;
